<!--Ocultar barra de navegacion inferior en book  H5P-->
<script>
document.addEventListener('DOMContentLoaded', function() {
  document.querySelectorAll('iframe.h5p-iframe').forEach(function(iframe) {
    iframe.addEventListener('load', function() {
      const style = iframe.contentDocument.createElement('style');
      style.textContent = `
        .h5p-interactive-book-status-footer {
          display: none !important;
        }
        .h5p-interactive-book-status-chapter {
          white-space: normal !important;
        }
        .h5p-interactive-book-status {
          padding: .6em !important;
        }
      `;
      iframe.contentDocument.head.appendChild(style);
    });
  });
});
